<script lang="ts">
	import { onMount } from 'svelte';

	function animatePath(pathname, animation) {
		let path;

		onMount(() => {
			path = document.querySelector(pathname);
			const length = path.getTotalLength();

			// Set Stroke Color
			path.style.stroke = '#d65d0e';

			// Clear any previous transition
			path.style.transition = path.style.WebkitTransition = 'none';

			// Set up the starting positions
			path.style.strokeDasharray = `${length} ${length}`;
			path.style.strokeDashoffset = `${length}`;

			// Trigger a layout so styles are calculated & the browser picks up the starting position before animating
			path.getBoundingClientRect();

			// Define our transition
			path.style.transition = path.style.WebkitTransition = animation;

			// Go!
			path.style.strokeDashoffset = '0';
		});
	}

	animatePath('#signature', 'stroke-dashoffset 5s ease-in-out');
</script>

